import { resolve } from 'path'
import { readFile } from 'fs'
import ResponseInfo from './response-info'
import ResourceLoader from './resource-loader'
import AppJsConverter from './app-js-converter'


/**
 * return Promise of ResponseInfo by URL
 */
export default class ContentResponder {


    constructor() {
        this.caches = {}
    }


    /**
     * create ResponseInfo
     * @param {string|Buffer} content
     * @param {string} [contentType=text/plain]
     * @param {number} [statusCode=200]
     * @return {Promise<ResponseInfo>}
     */
    respond(content, contentType, statusCode) {
        return Promise.resolve(new ResponseInfo(content, contentType, statusCode))
    }

    /**
     * create ResponseInfo of JSON
     * @param {Object} obj
     * @param {string} [contentType=application/json]
     * @param {number} [statusCode=200]
     * @return {Promise<ResponseInfo>}
     */
    respondJSON(obj, contentType = 'application/json', statusCode) {
        return this.respond(JSON.stringify(obj), contentType, statusCode)
    }


    /**
     * create ResponseInfo of file
     * @param {string} path
     * @param {string} [contentType=text/plain]
     * @param {number} [statusCode=200]
     * @return {Promise<ResponseInfo>}
     */
    respondFile(path, contentType, statusCode) {
        return new Promise((y, n) => {
            readFile(path, 'utf8', (e, o) => e ? n(e) : y(o))
        })
        .then(content => this.respond(content, contentType, statusCode))
    }

    /**
     * respond with cached content
     * @param {string} id identifier
     * @return {ResponseInfo}
     */
    responseCache(id) {
        return Promise.resolve(this.caches[id])
    }


    /**
     * check cache existence
     * @param {string} id identifier
     * @return {boolean}
     */
    hasCache(id) {
        return !!this.caches[id]
    }


    /**
     * cache the response
     * @param {string} id identifier
     * @param {ResponseInfo}
     * @return {ResponseInfo}
     */
    cache(id, responseInfo) {
        return this.caches[id] = responseInfo
    }

    /**
     * create ResponseInfo of NOT FOUND
     * @param {string} url
     * @return {Promise<ResponseInfo>}
     */
    notFound(url) {
        return this.respond(`404 not found: ${url}`, 'text/plain', 404)
    }


    /**
     * load web/index.html
     * @return {Promise<ResponseInfo>}
     */
    webUI() {
        return this.respondFile(__dirname + '/../../web/index.html', 'text/html')
    }

    /**
     * load dist/web/main.bundle.js
     * @return {Promise<ResponseInfo>}
     */
    webJS() {
        // if (this.hasCache('webJS')) return this.responseCache('webJS')

        const mainJSPath = resolve(__dirname, '../../dist/web/main.bundle.js')
        return this.respondFile(mainJSPath, 'text/javascript').then(info => this.cache('webJS', info))
    }

    /**
     * load a file in Resources directory
     * @param {string} url
     * @param {string} projDir
     * @param {string} platform
     * @return {Promise<ResponseInfo>}
     */
    resource(url, projDir, platform) {

        // @type Buffer
        let content = new ResourceLoader(url, projDir, platform).content
        if (content === null) {
            return this.notFound(url)
        }

        /**
         * In app.js, top variables are exported as global variables.
         * Thus, AppJsConverter converts the code as such.
         * TODO Caches the result.
         */
        if (url === '/app.js') {
            content = new AppJsConverter(content.toString()).convert()
        }

        return this.respond(content)
    }
}

"use strict";

import debug from 'debug'
import { readFileSync as read } from 'fs'
import http from 'http'
import { parse as urlParser } from 'url'
import {EventEmitter} from 'events'

import ContentResponder from './content-responder'

const P = f => new Promise(f)
const ____ = debug('faster-titanium:FileServer')
const ___x = debug('faster-titanium:FileServer:error')

/**
 * Serve resource files
 */
export default class FileServer extends EventEmitter {

    /**
     * @param {number} port
     * @param {string} token
     * @param {Array} routes
     */
    constructor(port, token, routes) {
        super()

        /** @type {number} */
        this.port = parseInt(port, 10)
        /** @type {string} */
        this.token = token
        /** @type {Array<Array>} */
        this.routes = routes
        /** @type {net.Socket[]} */
        this.sockets = []
        /** @type {http.Server} */
        this.server = http.createServer((req, res) => {
            this.parseBody(req, (err, body) =>
                err ? this.emit('error', err)
                    : this.onRequest(req, res, body))
        })

        this.server.on('error', err => ___x(err) || this.emit('error', err))
        this.server.on('connection', socket => this.sockets.push(socket))
    }

    get running() {
        return this.server.listening
    }


    /**
     * listen
     * @public
     * @return {Promise}
     */
    listen() {
        return P(y => this.server.listen(this.port, y)).then(x => {
            ____(`start listening ${this.port}`)
        })
    }


    /**
     * close server
     * @public
     * @return {Promise}
     */
    close() {
        ____(`terminating...`)
        this.sockets.forEach(socket => socket.destroy())
        return P(y => this.server.close(y)).then(x => {
            ____(`terminated`)
        })
    }

    /**
     * get instance of ResponseInfo with url matching passed routes
     * @param {string} url
     * @param {string} method
     * @param {string|Object} body
     * @return {Promise(ResponseInfo)}
     * @private
     */
    handleURL(url, method, body) {
        let getResponseInfo = null

        this.routes.some(route => {
            const pattern = route[0]
            const fn      = route[route.length - 1]
            const expectedMethod = (typeof route[1] === 'string') ? route[1] : 'GET'

            if ((typeof pattern === 'string' && pattern === url && method === expectedMethod) ||
                (pattern instanceof RegExp && url.match(pattern))) {
                getResponseInfo = fn
                return true
            }
            return false
        })

        if (getResponseInfo === null) { // not found
            return new ContentResponder().notfound()
        }
        return getResponseInfo(url, body)
    }

    /**
     * @param {http.ServerRequest} req
     * @param {http.ServerResponse} res
     * @param {string|Object} body
     * @private
     */
    onRequest(req, res, body) {

        const url = urlParser(req.url).pathname
        ____(`url: ${url}, method: ${req.method}, body: ${JSON.stringify(body)}`)

        this.handleURL(url, req.method, body)

        .then(responseInfo => {
            const { statusCode, contentType, content } = responseInfo
            ____({statusCode, contentType, length: content.length})

            res.writeHead(statusCode, {'Content-Type': contentType})
            res.write(content)
            res.end()
        })
        .catch(e => {
            res.writeHead(500, {'Content-Type': 'application/json'})
            res.write(JSON.stringify([err.message, err.stack]))
            res.end()
        })
    }

    /**
     * parse request body
     * @param {http.ServerRequest} req
     * @param {function(err: error, body: string|Object): void} fn
     */
    parseBody(req, cb) {

        if (req.method === 'GET') return cb(null, '')


        let body = ''
        req.setEncoding('utf8')
        req.on('error', e => cb(e))
        req.on('data', str => body += str)
        req.on('end', x => {
            if (req.headers['content-type'] === 'application/json') body = JSON.parse(body)
            cb(null, body)
        })
    }
}

"use strict";

import debug from 'debug'
import { join }  from 'path'
import { existsSync as exists, readFileSync as read } from 'fs'
import chokidar from 'chokidar'
import {EventEmitter} from 'events'
const ____ = debug('faster-titanium:FileWatcher')
const ___x = debug('faster-titanium:FileWatcher:error')

/**
 * Watch files
 */
export default class FileWatcher extends EventEmitter {

    /**
     * @param {string} projDir
     */
    constructor(projDir) {
        super()

        /** @type {string} path to the project dir */
        this.projDir = projDir
        /** @type {number} unix time(msec) of last emission of change event */
        this.lastEmission = 0
        /** @type {string} value (path) of last emission */
        this.lastEmitted = null
        /** @type {Object} chokidar object */
        this.watcher = chokidar.watch([], { ignoreInitial: true })

        this.watcher.on('change', ::this.onChange)
        this.watcher.on('error', path => ___x(path) || this.emit('error', path))
    }

    /**
     * start watching
     */
    watch() {
        ____(`start watching directories: ${this.dirs.join(', ')}`)
        this.watcher.add(this.dirs)
    }

    get watching() {
        return Object.keys(this.watcher.getWatched()).length > 0
    }


    /**
     * whether the last change event was emitted during the past 1000msec
     * @type {boolean}
     * */
    get justEmitted() {
        return new Date().getTime() - this.lastEmission < 1000
    }


    /**
     * stop watching
     */
    close() {
        this.watcher.close()
        ____(`watch stopped`)
    }

    /**
     * Called when a file is changed.
     * Sometimes watcher calls this twice to the same file, so memorizes past emission
     * @param {string} path
     */
    onChange(path) {
        if (this.lastEmitted === path && this.justEmitted) {
            ____(`preventing emission twice.`)
            return;
        }

        this.lastEmitted = path
        this.lastEmission = new Date().getTime()

        if (this.isAlloyPath(path)) {
            this.emit('change:alloy', path)
        }
        else {
            this.emit('change:Resources', path)
        }
    }


    isAlloyPath(path) {
        return path.indexOf(join(this.projDir, 'app/')) === 0
    }

    /** @type {string[]} */
    get dirs() {
        return [
            'Resources',
            'app',
            'i18n'
        ]
        .map( name => join(this.projDir, name) )
        .filter (exists)
    }
}

"use strict";

import debug from 'debug'
import chalk from 'chalk'
import randomstring from 'randomstring'
import FileServer from './file-server'
import FileWatcher from './file-watcher'
import NotificationServer from './notification-server'
import ContentResponder from './content-responder'
import AlloyCompiler from './alloy-compiler'
import Preferences from '../common/preferences'
import TiLog from './ti-log'
import { modNameByPath } from '../common/util'

const ____ = debug('faster-titanium:MainProcess')
const ___x = (e) =>
    debug('faster-titanium:MainProcess:error')(e) || debug('faster-titanium:MainProcess:error')(e.stack)


process.on('uncaughtException', (err) => {
    console.log(`Caught exception: ${err}`)
})


/**
 * main process
 */
export default class MainProcess {

    /**
     * @param {string} projDir
     * @param {Object} [options={}]
     * @param {number} [options.fPort] port number of the file server
     * @param {number} [options.nPort] port number of the notification server
     * @param {string} [options.host] host name or IP Address
     * @param {string} [options.platform] platform name (ios|android|mobileweb|windows)
     * @param {string} [options.token] identifier for this process
     */
    constructor(projDir, options = {}) {
        const { fPort, nPort, host, platform, tiDebug, token } = options

        /** @type {string} identifier for this process */
        this.token = token || randomstring.generate(10)
        /** @type {string} project dir */
        this.projDir = projDir
        /** @type {string} hostname or IP Address */
        this.host = host
        /** @type {string} platform os name of the Titanium App */
        this.platform = platform
        /** @type {Preferences} */
        this.prefs = new Preferences({tiDebug})
        /** @type {FileServer} */
        this.fServer = new FileServer(fPort, this.token, this.routes)
        /** @type {FileWatcher} */
        this.watcher = new FileWatcher(this.projDir)
        /** @type {NotificationServer} */
        this.nServer = new NotificationServer(nPort, this.token)
        /** @type {AlloyCompiler} */
        this.alloyCompiler = new AlloyCompiler(this.projDir, this.platform)
        this.registerListeners()
    }

    /** @type {string} */
    get restartCommand() {
        return `faster-ti restart -f ${this.fPort} -n ${this.nPort} -p ${this.platform} -t ${this.token} ${this.projDir}`
    }

    /** @type {string} */
    get url() {
        return `http://${this.host}:${this.fPort}/`
    }

    /** @type {number} */
    get fPort() {
        return this.fServer.port
    }

    /** @type {number} */
    get nPort() {
        return this.nServer.port
    }

    /**
     * register event listeners.
     * called only once in constructor
     * @private
     */
    registerListeners() {

        this.fServer.on('error', ___x)
        this.nServer.on('error', ___x)
        this.watcher.on('error', ___x)

        this.nServer.on('log', ::this.tilog)
        this.watcher.on('change:Resources', ::this.onResourceFileChanged)
        this.watcher.on('change:alloy', ::this.onAlloyFileChanged)

    }

    get running() {
        return this.fServer.running && this.nServer.running && this.watcher.watching

    }



    /**
     * launch file server and notification server
     * @return {Promise}
     * @public
     */
    launchServers() {
        ____(`launching servers`)
        return Promise.all([
            this.fServer.listen(),
            this.nServer.listen()
        ]).catch(___x)
    }

    /**
     * start watching files
     * @public
     */
    run() {
        this.watch()
        process.on('exit', x => {
            console.log(chalk.yellow(`You can restart faster-titanium server with the following command.\n`))
            console.log(chalk.yellow(this.restartCommand))
        })
    }


    /**
     * starting file watching
     * @private
     */
    watch() {
        ____(`starting file watcher`)
        this.watcher.watch()
    }



    /**
     * close servers and stop watching
     * @public
     */
    end() {
        ____(`terminating servers`)
        Promise.all([
            this.fServer.close(),
            this.nServer.close(),
            this.watcher.close()
        ])
    }

    /**
     * show titanium log in console
     * @param {Object} payload
     * @param {Array} payload.args
     * @param {string} payload.severity trace|debug|info|warn|error|critical
     * @param {Object} [payload.options]
     * @param {string} [payload.options.time] ISOStringfied time the log generated
     * @param {string} [payload.options.debugname] debug name
     */
    tilog(payload) {
        const { args, severity, options } = payload
        TiLog[severity](args, options)
    }


    /**
     * called when files in Resources directory changed
     * @param {string} path
     * @private
     */
    onResourceFileChanged(path) {
        if (this.alloyCompiler.compiling) return;

        ____(`changed: ${path}`)

        this.sendEvent({timer: 1000, names: [modNameByPath(path, this.projDir, this.platform)]})
    }

    /**
     * Called when files in app directory (Alloy project) changed
     * Compile alloy. During compilation, unwatch Resources directory.
     * @param {string} path
     * @private
     */
    onAlloyFileChanged(path) {

        ____(`changed:alloy ${path}`)

        const token = randomstring.generate(10) // identifier for this compilation

        this.send({event: 'alloy-compilation', token})

        const changedFiles = [] // files in Resources changed by alloy compilation
        const poolChanged = path => changedFiles.push(modNameByPath(path, this.projDir, this.platform))

        this.watcher.on('change:Resources', poolChanged)

        /** @type {AlloyCompiler} */
        return this.alloyCompiler.compile(path, token)
        .then(result => {
            this.send({event: 'alloy-compilation-done', token, success: result})
            this.watcher.removeListener('change:Resources', poolChanged)
            if (result) {
                this.sendEvent({timer: 500, names: changedFiles})
            }
        })
    }

    /**
     * send message to the client of notification server
     * @param {Object} payload
     * @param {string} payload.event event name. oneof alloy-compilation|alloy-compilation-done|reload|reflect|debug-mode
     */
    send(payload) {
        console.assert(payload && payload.event)
        this.nServer.send(payload)
    }


    /**
     * send reload|reflect event to the titanium client
     * @param {Object} [options={}]
     * @return {Promise}
     * @private
     */
    sendEvent(options = {}) {
        let eventName;

        switch (this.prefs.style) {
            case 'manual':
                return
            case 'auto-reload':
                eventName = 'reload'
                break
            case 'auto-reflect':
                eventName = 'reflect'
                break
        }

        const payload = Object.assign({}, {event: eventName}, options)

        this.send(payload)
    }


    /**
     * @type {Array}
     * @todo separate this to another file
     */
    get routes() {
        const responder = new ContentResponder()
        return [
            ['/', url =>
                responder.webUI()],

            ['/info', url =>
                responder.respondJSON(this.info)],

            ['/prefs', 'POST', (url, body) => {
                this.prefs.apply(body)
                this.send({event: 'preferences', prefs: this.prefs})
                return responder.respondJSON(this.prefs)
            }],

            ['/prefs', 'GET', (url) => responder.respondJSON(this.prefs) ],


            ['/kill', url => {
                process.nextTick(::this.end)
                return responder.respond()
            }],

            ['/reload', url => {
                this.send({event: 'reload', force: true})
                return responder.respond()
            }],

            ['/faster-titanium-web-js/main.js', url => responder.webJS()],

            [/^\//, url => // any URL
                responder.resource(url, this.projDir, this.platform)],
        ]
    }


    /**
     * information of FasterTitanium process
     * @type {Object}
     */
    get info() {
        return {
            'access token'                    : this.token,
            'project root'                    : this.projDir,
            'notification server port'        : this.nPort,
            'process uptime'                  : parseInt(process.uptime()) + ' [sec]',
            'platform'                        : this.platform,
            'connection with client'          : this.nServer.connected,
            'loading style'                   : this.prefs.style,
            'show debug log in Titanium'      : this.prefs.tiDebug,
            'show ti log in server console'   : this.prefs.serverLog,
            'show ti log in titanium console' : this.prefs.localLog,
            //'Reloaded Times'               : this.stats.reloadedTimes
        }
    }
}

import debug from 'debug'
import net from 'net'
import FileWatcher from './file-watcher'
import {EventEmitter} from 'events'
const P = f => new Promise(f)
const ____ = debug('faster-titanium:NotificationServer')
const ___x = debug('faster-titanium:NotificationServer:error')

/**
 * Server connecting continuously with Titanium App.
 * Restrict connection: only one device can connect to the server.
 */
export default class NotificationServer extends EventEmitter {

    /**
     * @param {string} [port]
     */
    constructor(port, token) {
        super()
        this.port = port
        this.token = token
        this.client = null
        this.server = net.createServer(::this.verifyClient)
        this.server.on('error', err => ___x(err) || this.emit('error', err))
        /** @type {string} received pre-parsed text */
        this.received = ''
    }


    /** @type {boolean} */
    get connected() {
        return !!(this.client && this.client.writable)
    }


    get running() {
        return this.server.listening
    }

    /**
     * listen
     * @public
     * @return {Promise}
     */
    listen() {
        return P(y => this.server.listen(this.port, y)).then(x =>{
            ____(`start listening ${this.port}`)
        })
    }

    /**
     * close server
     * @public
     * @return {Promise}
     */
    close() {
        ____(`terminating...`)
        this.client && this.client.destroy()

        return P(y => this.server.close(y)).then(x => {
            ____(`terminated`)
        })
    }

    /**
     * check the client has the right access token
     * @param {net.Socket} socket
     */
    verifyClient(socket) {
        socket.setEncoding('utf8')
        socket.once('data', (clientToken) => {
            if (clientToken === this.token) {
                this.addClient(socket)
            }
            else {
                ____(`Token doesn't match. client: ${clientToken}, server: ${this.token}.
                     For most case, this is caused by older app retrying connection to the server. Ignore it.`)
                socket.end()
            }
        })
    }

    /**
     * add a client socket
     * @param {net.Socket} socket
     */
    addClient(socket) {
        if (this.client) {
            ____(`New connection, Overwrite existing connection.`)
            if (this.client.writable) { this.client.end() }
        }
        else {
            ____(`New connection. Set client.`)
        }
        socket.on('data', ::this.onData)
        this.client = socket
        this.send({event: 'connected'})
    }


    /**
     * send payload to the client
     * @param {object} [payload={}]
     */
    send(payload = {}) {
        if (!this.client) {
            return ____(`sending message suppressed: No client.`)
        }
        if (!this.client.writable) {
            this.client = null
            return ____(`sending message suppressed: Socket is not writable.`)
        }

        ____(`sending payload: ${JSON.stringify(payload)}\n`)
        // as payloads are sometimes joined with previous one, the client should split them with "\n" separator
        // (see src/titanium/socket.js)
        this.client.write(JSON.stringify(payload) + '\n')
    }

    /**
     * Called when data comes from client
     * Split into multiple payloads
     * @param {string} chunk
     */
    onData(chunk) {
        this.received += chunk
        const payloads = this.received.split('\n')
        this.received = payloads.pop()

        payloads.map(JSON.parse).forEach(::this.readPayload)
    }

    /**
     * read payload sent by client
     * @param {Object} payload
     */
    readPayload(payload) {

        switch (payload.type) {
            case 'log':
                this.emit('log', payload)
                break
            default:
                // console.log(payload)
                break
        }
    }
}

/*
 * following codes are extracted and modified from alloy/Alloy/commands/compile/index.js
 */

import { existsSync, readFileSync as read, writeFileSync as write } from 'fs'
import path from 'path'
path.existsSync = existsSync // for backward compatibility

import uglifyjs from 'uglify-js'
import {getPlatformDirname} from '../common/util'
import CONST from 'alloy/Alloy/common/constants'
import { getAndValidateProjectPaths as getPaths } from 'alloy/Alloy/utils'
import { OPTIONS_OUTPUT as outputOptions } from 'alloy/Alloy/commands/compile/sourceMapper'

import debug from 'debug'
const ____ = debug('faster-titanium:optimizeAlloy')
const ___x = debug('faster-titanium:optimizeAlloy:error')


/**
 * optimize js file in Alloy project and save it to Resources
 *
 * @issue it now re-writes js files other than alloy.js
 * @todo refactoring much of it!!!
 * @param {string} projDir
 * @param {string} relPath relative path from projDir
 * @param {object} alloyConfig
 * @param {string} [alloyConfig.platform]
 * @param {string} [alloyConfig.deploytype] development
 */
export default function optimizeAlloy(projDir, relPath, alloyConfig) {

    const paths = getPaths(projDir)

    const platformDirname = getPlatformDirname(alloyConfig.platform)

    const file = path.join(platformDirname, relPath.slice(4))

    const compileConfig = { alloyConfig, dir: paths }

    const fullpath = path.join(compileConfig.dir.resources, file)

    ____(`optimizing ${platformDirname}/${relPath}`)

    let ast = uglifyjs.parse(read(fullpath, 'utf8'), { filename: file })
    ast = [ 'builtins', 'optimizer' ]

        .map(modName => require('alloy/Alloy/commands/compile/ast/' + modName))

        .reduce((ast, mod) => {
            ast.figure_out_scope()
            return mod.process(ast, compileConfig) || ast
        }, ast)

    ast = compress(ast, alloyConfig)

    let stream = uglifyjs.OutputStream(outputOptions)
    ast.print(stream)
    write(fullpath, stream.toString())
}

// modify alloy/Alloy/commands/compile/ast/compress.js
function compress(ast, alloyConfig) {

    const { target, deploytype, platform } = alloyConfig

    // create list of platform and deploy type defines
    const defines = {}

    CONST.DEPLOY_TYPES.forEach(d => {
        defines[d.key] = deploytype === d.value
    })
    CONST.DIST_TYPES.forEach(d => {
        defines[d.key] = d.value.indexOf(target) >= 0
    })
    CONST.PLATFORMS.forEach(p => {
        defines['OS_' + p.toUpperCase()] = platform === p
    })

    const compressor = uglifyjs.Compressor({
        sequences     : false,  // join consecutive statemets with the comma operator
        properties    : false,   // optimize property access: a["foo"]  a.foo
        dead_code     : true,   // discard unreachable code
        drop_debugger : false,   // discard debugger statements
        unsafe        : false,   // some unsafe optimizations (see below)
        conditionals  : true,   // optimize if-s and conditional expressions
        comparisons   : true,   // optimize comparisons
        evaluate      : true,   // evaluate constant expressions
        booleans      : false,   // optimize boolean expressions
        loops         : false,   // optimize loops
        unused        : true,   // drop unused variables/functions
        hoist_funs    : true,   // hoist function declarations
        hoist_vars    : false,  // hoist variable declarations
        if_return     : false,   // optimize if-s followed by return/continue
        join_vars     : false,   // join var declarations
        cascade       : false,   // try to cascade `right` into `left` in sequences
        side_effects  : false,   // drop side-effect-free statements
        warnings      : false,   // warn about potentially dangerous optimizations/code
        global_defs   : defines      // global definitions
    })

    return ast.transform(compressor)
}

"use strict";

import { join }  from 'path'
import { existsSync as exists, readFileSync as read } from 'fs'
import debug from 'debug'
import {getPlatformDirname} from '../common/util'

const ____ = debug('faster-titanium:ResourceLoader')
const ___x = debug('faster-titanium:ResourceLoader:error')

/**
 * Get contents of Titanium Resources
 */
export default class ResourceLoader {

    /**
     * @param {string} url
     * @param {string} projDir
     * @param {string} platform
     */
    constructor(url, projDir, platform) {
        /** @type {string} */
        this.projDir = projDir
        /** @type {string} */
        this.platformDirname = getPlatformDirname(platform)
        /** @type {string} */
        this.url = url
    }


    /** @type {Buffer} */
    get content() {
        return this.load(this.platformPath) || this.load(this.commonPath) || null
    }

    load(path) {
        if (exists(path)) {
            ____(`path: ${path}`)
            return read(path) // TODO cache results
        }
        return null
    }

    get platformPath() {
        return join(this.projDir, 'Resources', this.platformDirname, this.url)
    }

    get commonPath() {
        return join(this.projDir, 'Resources', this.url)
    }

     /** @type {boolean} */
    get exists() {
        return exists(this.path) // TODO cache results
    }
}

/**
 * value object containing statusCode, contentType, content
 */
export default class ResponseInfo {
    constructor(content = '', contentType = 'text/plain', statusCode = 200) {
        this.content     = content
        this.contentType = contentType
        this.statusCode  = statusCode
    }
}

/**
 * Logger titanium logs into server console
 */
export default class TiLog {

    static log(args, options) {
        this.__base('INFO', '', '', args, options)
    }

    static info(args, options) {
        this.__base('INFO', '', '', args, options)
    }

    static warn(args, options) {
        this.__base('WARN', '\u001b[33m', '\u001b[0m', args, options)
    }

    static debug(args, options) {
        this.__base('DEBUG', '\x1b[38;5;240m', '\x1b[0m', args, options)
    }

    static trace(args, options) {
        this.__base('TRACE', '\x1b[38;5;233m', '\x1b[0m', args, options)
    }

    static error(args, options) {
        this.__base('ERROR', '\u001b[31m', '\u001b[0m', args, options)
    }

    static critical(args, options) {
        this.__base('CRITICAL', '\u001b[31m', '\u001b[0m', args, options)
    }


    static __base(name, startANSI, endANSI, args, options = {}) {
        const {time, debugname} = options
        const title = debugname ? debugname + ':' + name : name

        const consoleArgs = [startANSI + `[Ti:${title}]`, ...args]

        if (time) {
            const timediff = new Date().getTime(time) - new Date().getTime()
            if (timediff < 0) consoleArgs.push(`\u001b[36m${timediff}ms\u001b[0m`) // cyan
        }
        if (endANSI) consoleArgs.push(endANSI)

        console.log.apply(console, consoleArgs)
    }
}

